package java_basics;

public class Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number = 5;
		System.out.println(number);
		
		number = 6;
		System.out.println(number);
		System.out.println(number + number);
		System.out.println(number / number);

		
		String words = "Hello";
		
		System.out.println(words);
		System.out.println(words + " world");


		
		

	}

}
